package uz.gita.newsapp_practice.data.source.local.pref.implimport android.content.Contextimport dagger.hilt.android.qualifiers.ApplicationContextimport uz.gita.newsapp_practice.data.source.local.pref.MySharedPrefimport javax.inject.Injectclass MySharedPrefImpl @Inject constructor(    @ApplicationContext private val context: Context) : MySharedPref {    private val sharedPrefs = context.getSharedPreferences("MyPrefs", Context.MODE_PRIVATE)    override fun isFirstLaunch(): Boolean {        return sharedPrefs.getBoolean("is_first_launch", true)    }    override fun setFirstLaunch(isFirstLaunch: Boolean) {        sharedPrefs.edit().putBoolean("is_first_launch", isFirstLaunch).apply()    }}