package uz.gita.newsapp_practice.diimport dagger.Bindsimport dagger.Moduleimport dagger.hilt.InstallInimport dagger.hilt.components.SingletonComponentimport uz.gita.newsapp_practice.domain.usecase.CategoriesUseCaseimport uz.gita.newsapp_practice.domain.usecase.SliderNewsUseCaseimport uz.gita.newsapp_practice.domain.usecase.impl.CategoriesUseCaseImplimport uz.gita.newsapp_practice.domain.usecase.impl.SliderNewsUseCaseImplimport javax.inject.Singleton@Module@InstallIn(SingletonComponent::class)interface UseCaseModule {    @[Binds Singleton]    fun bindsUseCase(impl: SliderNewsUseCaseImpl): SliderNewsUseCase    @[Binds Singleton]    fun bindsCategoryUseCase(impl: CategoriesUseCaseImpl): CategoriesUseCase}