package uz.gita.newsapp_practice.domain.usecase.implimport kotlinx.coroutines.flow.Flowimport uz.gita.newsapp_practice.domain.models.CategoryParamimport uz.gita.newsapp_practice.domain.repository.NewsRepositoryimport uz.gita.newsapp_practice.domain.usecase.CategoriesUseCaseimport javax.inject.Injectclass CategoriesUseCaseImpl @Inject constructor(    private val repository: NewsRepository,) : CategoriesUseCase {    override fun invoke(category: String): Flow<List<CategoryParam>> =        repository.getCategories(category)}