package uz.gita.newsapp_practice.presenter.screens.detail.newsimport androidx.lifecycle.ViewModelimport androidx.lifecycle.viewModelScopeimport dagger.hilt.android.lifecycle.HiltViewModelimport kotlinx.coroutines.flow.MutableStateFlowimport kotlinx.coroutines.launchimport javax.inject.Inject@HiltViewModelclass NewsViewModel @Inject constructor(    private val newsDirection: NewsDirection,) : NewsContract.ViewModel, ViewModel() {    override val uiState = MutableStateFlow<NewsContract.UIState>(NewsContract.UIState())    override fun onEventDispatcher(intent: NewsContract.Intent) {        when (intent) {            NewsContract.Intent.BackToMain -> {                viewModelScope.launch {                    newsDirection.backToMain()                }            }        }    }}