package uz.gita.newsapp_practice.presenter.screens.detail.sliderimport androidx.lifecycle.ViewModelimport androidx.lifecycle.viewModelScopeimport dagger.hilt.android.lifecycle.HiltViewModelimport kotlinx.coroutines.flow.MutableStateFlowimport kotlinx.coroutines.launchimport uz.gita.newsapp_practice.domain.usecase.SliderNewsUseCaseimport javax.inject.Inject@HiltViewModelclass SliderScreenViewModel @Inject constructor(    private val useCase: SliderNewsUseCase,    private val sliderDirection: SliderDirection,) : SliderScreenContract.ViewModel, ViewModel() {    override val uiState = MutableStateFlow(SliderScreenContract.UIState())    override fun onEventDispatcher(intent: SliderScreenContract.Intent) {        viewModelScope.launch {            sliderDirection.backToMain()        }    }}