package uz.gita.newsapp_practice.presenter.screens.introimport uz.gita.newsapp_practice.presenter.screens.main.MainScreenimport uz.gita.newsapp_practice.utils.navigation.AppNavigatorimport javax.inject.Injectinterface IntroDirection {    suspend fun openMainScreen()}class IntroDirectionImpl @Inject constructor(    private val appNavigator: AppNavigator,) : IntroDirection {    override suspend fun openMainScreen() {        appNavigator.openWithoutSave(MainScreen())    }}