package uz.gita.newsapp_practice.presenter.screens.introimport androidx.lifecycle.ViewModelimport androidx.lifecycle.viewModelScopeimport dagger.hilt.android.lifecycle.HiltViewModelimport kotlinx.coroutines.launchimport uz.gita.newsapp_practice.data.source.local.pref.MySharedPrefimport javax.inject.Inject@HiltViewModelclass IntroViewModel @Inject constructor(    private val introDirection: IntroDirection,    private val sharedPref: MySharedPref,) : IntroContract.ViewModel, ViewModel() {    override fun onEventDispatcher(intent: IntroContract.Intent) {        when (intent) {            IntroContract.Intent.MoveToMain -> {                viewModelScope.launch {                    sharedPref.setFirstLaunch(false)                    introDirection.openMainScreen()                }            }        }    }}