package uz.gita.newsapp_practice.presenter.screens.mainimport kotlinx.coroutines.flow.StateFlowimport uz.gita.newsapp_practice.domain.models.CategoryParamimport uz.gita.newsapp_practice.domain.models.SliderParaminterface MainContract {    interface ViewModel {        val uiState: StateFlow<UIState>        fun onEventDispatcher(intent: Intent)    }    data class UIState(        val sliderNews: List<SliderParam> = emptyList(),        val categoriesList: List<String> = listOf(            "bbc-news",            "abc-news",            "usa-today",            "the-washington-post",            "google-news",            "cnn",            "associated-press",            "reuters",            "al-jazeera-english",            "the-huffington-post",            "axios",            "the-globe-and-mail",            "fortune",            "the-hill",            "aftenposten",            "vice-news",            "spiegel-online",            "the-jerusalem-post",        ),        val newsList: List<CategoryParam> = emptyList(),        val selectedCategory: String = "bbc-news",    )    interface Intent {        data class SliderDetail(val sliderParam: SliderParam) : Intent        data class SelectCategory(val category: String) : Intent        data class NewsDetail(val categoryParam: CategoryParam) : Intent    }}