package uz.gita.newsapp_practice.presenter.screens.splashimport uz.gita.newsapp_practice.presenter.screens.intro.IntroScreenimport uz.gita.newsapp_practice.presenter.screens.main.MainScreenimport uz.gita.newsapp_practice.utils.navigation.AppNavigatorimport javax.inject.Injectinterface SplashDirection {    suspend fun moveToMainScreen()    suspend fun moveToIntroScreen()}class SplashDirectionImpl @Inject constructor(    private val appNavigator: AppNavigator,) : SplashDirection {    override suspend fun moveToMainScreen() {        appNavigator.openWithoutSave(MainScreen())    }    override suspend fun moveToIntroScreen() {        appNavigator.openWithoutSave(IntroScreen())    }}