package uz.gita.newsapp_practice.presenter.screens.splashimport android.annotation.SuppressLintimport androidx.compose.foundation.Imageimport androidx.compose.foundation.backgroundimport androidx.compose.foundation.layout.Boximport androidx.compose.foundation.layout.fillMaxSizeimport androidx.compose.foundation.layout.heightimport androidx.compose.foundation.layout.widthimport androidx.compose.runtime.Composableimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.res.painterResourceimport androidx.compose.ui.tooling.preview.Previewimport androidx.compose.ui.unit.dpimport cafe.adriel.voyager.androidx.AndroidScreenimport cafe.adriel.voyager.hilt.getViewModelimport uz.gita.newsapp_practice.Rclass SplashScreen : AndroidScreen() {    @Composable    override fun Content() {        getViewModel<SplashViewModel>()        SplashScreenContent()    }}@SuppressLint("InvalidColorHexValue")@Composablefun SplashScreenContent() {    Box(modifier = Modifier.fillMaxSize().background(Color(0xFFE9EEFA))) {        Image(            painter = painterResource(id = R.drawable.lask_logo),            contentDescription = "Main Logo",            modifier = Modifier                .align(Alignment.Center)                .height(169.dp)                .width(109.dp)        )    }}@Composable@Preview(showBackground = true)fun SplashScreenPreview() {    SplashScreenContent()}