package uz.gita.newsapp_practice.presenter.screens.splashimport androidx.lifecycle.ViewModelimport androidx.lifecycle.viewModelScopeimport dagger.hilt.android.lifecycle.HiltViewModelimport kotlinx.coroutines.delayimport kotlinx.coroutines.launchimport uz.gita.newsapp_practice.data.source.local.pref.MySharedPrefimport javax.inject.Inject@HiltViewModelclass SplashViewModel @Inject constructor(    private val direction: SplashDirection,    private val sharedPref: MySharedPref) : SplashContract.ViewModel, ViewModel() {    init {        viewModelScope.launch {            delay(2000L)            if (sharedPref.isFirstLaunch()) {                direction.moveToIntroScreen()            } else {                direction.moveToMainScreen()            }        }    }}