package uz.gita.newsapp_practice.utilsimport android.net.ConnectivityManagerimport android.net.NetworkCapabilitiesfun ConnectivityManager.isNetworkConnected(): Boolean {    val networkCapabilities = this.activeNetwork ?: return false    val networkInfo = this.getNetworkCapabilities(networkCapabilities) ?: return false    return networkInfo.hasCapability(NetworkCapabilities.NET_CAPABILITY_INTERNET)}